import colorizeimport datetimeanonas = int(input("\033[1;32m Ano de nascimento:\033[m"))atual = datetime.date.today().yeardata = anonas + 18idade = atual - anonasalis = 18 - idadeatrasado = idade - 18print("Quem nasceu em {} tem {} anos em {}." .format(anonas, (atual - anonas), atual))if idade < 18:    print("Ainda falta {} anos para o alistamento" .format(alis))    print("Seu alistamento será em {}".format(data))elif idade == 18:    print("\033[1;31mVocê tem que se alistar IMEDIATAMENTE!\033[m")elif idade > 18:    print("\033[1;31m ATENÇÃO! \033[m")    print("\033[1;31mVocê já deveria ter se alistado há {} anos.\033[m" .format(atrasado))    print("\033[1;31mSeu alistamento foi em {}\033[m" .format(data))